mapper.select:
select date_format(a.click_date,'%m-%d') as click_date,ifnull(b.count,0) as count from (
SELECT date_sub(curdate(), interval 4 day) as click_date union all SELECT date_sub(curdate(), interval 3 day) as click_date union all SELECT date_sub(curdate(), interval 2 day) as click_date union all SELECT date_sub(curdate(), interval 1 day) as click_date union all SELECT curdate() as click_date
) a left join ( select date(c_time) as datetime, count(*) as count ,ostate from orders where ostate BETWEEN 11 and 39 group by date(c_time) ) b on a.click_date = b.datetime ORDER BY click_date asc

select date_format(a.click_date,'%m-%d') as click_date,ifnull(b.count,0) as count from (
SELECT date_sub(curdate(), interval 4 day) as click_date union all SELECT date_sub(curdate(), interval 3 day) as click_date union all SELECT date_sub(curdate(), interval 2 day) as click_date union all SELECT date_sub(curdate(), interval 1 day) as click_date union all SELECT curdate() as click_date
) a left join ( select date(c_time) as datetime, count(*) as count ,ostate from orders where ostate =40 group by date(c_time) ) b on a.click_date = b.datetime ORDER BY click_date asc

select COUNT(o.id) as count from orders o  INNER JOIN movie_showtime ms
 on  o.movie_showtime_id = ms.id INNER JOIN cinema c on ms.cid =c.id
  WHERE c.aid=#{aid,jdbcType=INTEGER}
